import React, { Component } from 'react';
import './App.css';


class Hotel extends Component {
	constructor(props) {
    super(props);
    this.state = {  
    				"rooms":1,"adults":1,"children":0
    			 };

   	}

   	manageRoom(actionType){
   		if(actionType == 'add'){

   			var roomAdd = this.state.rooms < 5 ? this.state.rooms + 1 : 5;
   			var adultAdd = roomAdd > this.state.adults  ?  roomAdd : this.state.adults;

   			this.setState({rooms:roomAdd, adults:adultAdd});
   		}
   		else{

   			var roomMinus = this.state.rooms >1 ? this.state.rooms - 1 : 1;
   			if(roomMinus*4 < (this.state.adults + this.state.children) )
   			{
   				var childM = this.state.children -1 > 0 ? this.state.children-1 : "0";
   				var adultM = (roomMinus*4) ;
   			}
   			//var adultM =  this.state.adults - 1;
   			//var childM =  this.state.children - 1;
   			this.setState({rooms:roomMinus,adults:adultM,children:childM});
   		}
   	}

   	manageAdult(actionType){
   		console.log(this.state.children);
   		if(actionType == 'add'){
   			var adultAdd = this.state.adults + 1;
   			var calRoom = Math.ceil((adultAdd + this.state.children)/4);
   			console.log(calRoom)
   			this.setState({adults:adultAdd,rooms:(calRoom <= 5 ? calRoom : 5)});
   		}
   		else{

   			var adultMinus = (this.state.adults >1 ? this.state.adults-1  : 1);
   			var calRoom = adultMinus > 1 ? Math.ceil((adultMinus + this.state.children)/4) : 1;
   			console.log(calRoom)
   			this.setState({adults:adultMinus,rooms:calRoom });
   		}

   		//this.roomLogic();
   	}

   	manageChild(actionType){
   		if(actionType == 'add'){
   			var childAdd = this.state.children + 1;
   			var calRoom = Math.ceil((childAdd + this.state.adults)/4);
   			console.log(calRoom)
   			this.setState({children:childAdd,rooms:(calRoom <= 5 ? calRoom : 5)});
   		}
   		else{

   			var childMinus = this.state.children - 1;
   			var calRoom = childMinus > 1 ? Math.ceil((childMinus + this.state.adults)/4) : 1;
   			console.log(calRoom)
   			this.setState({children:(childMinus >1 ? childMinus  : 0),rooms:(calRoom <= 5 ? calRoom : 5)});
   		}

   		//this.roomLogic();
   	}

   	roomLogic(){

   		let needAdults,needChildren,needRoom;
   		//this.state.children
   		let totalUser=this.state.children + this.state.adults;
   		needRoom = Math.ceil(totalUser/4);
   		//needRoom=needRoom > this.state.rooms ? needRoom : this.state.rooms;


   		if(needRoom < this.state.rooms && totalUser < this.state.rooms){
   			needAdults=this.state.rooms;
   			needChildren=this.state.adults;
   		}


   		this.setState({children:needChildren,rooms:needRoom,adultMinus:needAdults});


   	}

  render() {
    return (

     <div className="row text-center">
     <div className="col-md-12">
      <p className="title">Choose number of <b>people</b></p>
      <div className="mainDiv">



     	<div className="row">
       	<div className="col-sm-4">Rooms</div>
       	<div  className="col-sm-8">
       		<a href="Javascript:Void(0);" onClick={(e)=>this.manageRoom('minus')}> 
     			<span class="glyphicon glyphicon-minus-sign"></span>
			</a>
			<span className="bui-stepper__display" data-bui-ref="input-stepper-value" aria-hidden="true">
			{this.state.rooms}</span>
			<a href="Javascript:Void(0);"  onClick={(e)=>this.manageRoom('add')} className="addSign">
			<span class="glyphicon glyphicon-plus-sign"></span>
			</a>
		</div>
		</div>

<hr></hr>

      	<div className="row">
       	<div className="col-sm-4">Adults</div>
       	<div  className="col-sm-8">
       		<a href="Javascript:Void(0);" onClick={(e)=>this.manageAdult('minus')}> 
     			<span class="glyphicon glyphicon-minus-sign"></span>
			</a>
			<span className="bui-stepper__display" data-bui-ref="input-stepper-value" aria-hidden="true">{this.state.adults}</span>
			<a href="Javascript:Void(0);"  onClick={(e)=>this.manageAdult('add')} className="addSign">
			<span class="glyphicon glyphicon-plus-sign"></span>
			</a>
		</div>
		</div>

<hr></hr>


      	<div className="row">
       	<div className="col-sm-4">Children</div>
       	<div  className="col-sm-8">
     		<a href="Javascript:Void(0);" onClick={(e)=>this.manageChild('minus')}> 
     			<span class="glyphicon glyphicon-minus-sign"></span>
			</a>
			<span className="bui-stepper__display" data-bui-ref="input-stepper-value" aria-hidden="true">{this.state.children}</span>
			<a href="Javascript:Void(0);"  onClick={(e)=>this.manageChild('add')}  className="addSign">
				<span class="glyphicon glyphicon-plus-sign"></span>

			</a>
			</div>
			</div>
       </div>
       </div>

       </div>
      );
  }
}

export default Hotel;
