var state = { data:[
  {
      "id": "0001",
      "price": 500,
      "product": "Cake",
      "quantity": 8
  },
  {
      "id": "0002",
      "price": 10,
      "product": "Patise",
      "quantity": 4
  },
  {
      "id": "0003",
      "price": 50,
      "product": "Masala Dosa",
      "quantity": 3
  }
]}
const users = (state, action) => {
    switch (action.type) {
      case 'SHOW_LIST':
        return [
          ...state,
          {
            id: action.id,
            text: action.text,
            completed: false
          }
        ]
      case 'TOGGLE_TODO':
        return state.map(todo =>
          todo.id === action.id ? { ...todo, completed: !todo.completed } : todo
        )
      default:
        return state
    }
  }

  export default users
