      delete: (id) => dispatch({ type: 'DELETE',data:id }),
      edit: () => dispatch({ type: 'EDIT' }),
     // reset: () => dispatch({ type: 'RESET' })
    }
  }

//export default Edit;
export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Edit)
