import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";


class Newcard extends Component {
   constructor() {
    super();
    this.state = {
      number: '',
      month: '',
      year: '',
      cvv: '',
      name: '',

    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChangeNumber = this.handleChangeNumber.bind(this);
     this.handleChangeMonth = this.handleChangeMonth.bind(this);
    this.handleChangeYear = this.handleChangeYear.bind(this);
    this.handleChangeCvv = this.handleChangeCvv.bind(this);
    this.handleChangeName = this.handleChangeName.bind(this);

  }

   handleChangeName(e) {
        this.setState({name: e.target.value});
    }
   handleChangeNumber(e) {
        this.setState({number: e.target.value});
    }
   handleChangeMonth(e) {
        this.setState({month: e.target.value});
    }
   handleChangeYear(e) {
        this.setState({year: e.target.value});
    }
   handleChangeCvv(e) {
        this.setState({cvv: e.target.value});
    }

  handleSubmit() {
    console.log(this.state.number+this.state.name+this.state.month+this.state.year+this.state.cvv);

    event.preventDefault();
  }
  render() {
    return (
         <div>
         <form className="credit-card" onSubmit={this.handleSubmit}>
            <div className="form-header">
              <h4 className="title">Credit card detail</h4>
            </div>

            <div className="form-body">
              <input  type="number" pattern="[0-9]*"  onChange={this.handleChangeNumber} className="card-number" placeholder="Card Number" />

              <div className="date-field">
                <div className="month">
                  <select name="Month"  onChange={this.handleChangeMonth}>
                    <option value="january">January</option>
                    <option value="february">February</option>
                    <option value="march">March</option>
                    <option value="april">April</option>
                    <option value="may">May</option>
                    <option value="june">June</option>
                    <option value="july">July</option>
                    <option value="august">August</option>
                    <option value="september">September</option>
                    <option value="october">October</option>
                    <option value="november">November</option>
                    <option value="december">December</option>
                  </select>
                </div>
                <div className="year"  onChange={this.handleChangeYear}>
                  <select name="Year">
                    <option value="2016">2016</option>
                    <option value="2017">2017</option>
                    <option value="2018">2018</option>
                    <option value="2019">2019</option>
                    <option value="2020">2020</option>
                    <option value="2021">2021</option>
                    <option value="2022">2022</option>
                    <option value="2023">2023</option>
                    <option value="2024">2024</option>
                  </select>
                </div>
 /              </div>

              <div className="card-verification">
                <div className="cvv-input">
                  <input type="number" placeholder="CVV" onChange={this.handleChangeCvv} />
                </div>
                <div className="cvv-details">
                 <input type="text" placeholder="Name this card for future use"  onChange={this.handleChangeName}/>
                </div>
              </div>

              <button type="submit" className="proceed-btn"><a href="#">SAVE THIS CARD</a></button>
              <input type="reset" className="paypal-btn" value="CANCEL" name="Cancel" />
              <p className="note">Your card details would be securely saved for faster payments.Your CVV will not be stored.</p>

            </div>
             </form>
         </div>
    );
  }
}

export default Newcard;
