import React, { Component } from 'react';
//import { BrowserRouter as Router, Route, Link } from "react-router-dom";
//import Newcard from './Newcard';


class UserManager extends Component {
constructor(props) {
    super(props);
    this.state = {  
    				data : [
    						{name:"xyz",email:"xyz@xyz.com",id:1},
    						{name:"abc",email:"abc@abc.com",id:2}
    						],
    				userEdit : {name:"",email:"",id:""},
    				flag:'view'
    			 };

   	this.handleChange = this.handleChange.bind(this);
   	this.handleUpdateInfo = this.handleUpdateInfo.bind(this);
   	}

  handleDelete(id){
  	console.log(id)
 // var data = 	this.state.data.filter(data => data.id !== id );
  	this.setState({data : this.state.data.filter(data => data.id !== id )});
//console.log(data)
  }

  handleEdit(id){

  	var user = this.state.data.filter(data => data.id === id );
  	// console.log(user[0])
  	// var userObj = user[0];
  	this.setState({userEdit:Object.assign({},user[0]),flag:"add"});
  }

  handleChange(e){
  		//var inputData = {[e.target.name]:e.target.value};
  		 var userObj  = this.state.userEdit;
  		 userObj[e.target.name] = e.target.value;
  		this.setState({userEdit:userObj});
  }

  handleUpdateInfo(){
  //console.log(this.state.userEdit)
  if(this.state.userEdit.id !=""){
  var userEditData  = [];
	var userEditData = this.state.data.filter(data => data.id !== this.state.userEdit.id );
	 userEditData.push(this.state.userEdit);
 //console.log(userEditData)
 this.setState({data:userEditData,flag:"view"});
}
else{

	var newArr = this.state.data;
	 var userArr  = Object.assign({},this.state.userEdit);
  		 userArr["id"] = this.state.data.length + 1;

	 newArr.push(userArr);
	console.log(newArr);
	 this.setState({data:newArr,flag:"view",userEdit:{name:"",email:"",id:""}});

}

  	// var newArr=[];
  	// for(var i=0; i<this.state.data.length;i++){

  	// 	if(this.state.data[i]['id']==this.state.userEdit.id){
  	// 		newArr.push(this.state.userEdit);
  	// 	}else{
  	// 		newArr.push(this.state.data[i])
  	// 	}
  	// }

  //	this.setState({data:newArr});

  }

visible(flag){
	this.setState({flag:flag})
}

  render() {
    return (
    <div>

    { this.state.flag == 'view' ? this.List() : this.Add()}
     </div>

    );
  }


  List(){
  	return(
			<div className="App">
<button onClick={(e)=>this.visible('add')}>Add New</button>
			<h4>List</h4>
		         <table border="2">
		          <tbody>
		          	<tr><th>Id</th><th>Name</th><th>Email</th><th>Action</th></tr>
		          	{this.state.data.map((data)=> 
		          				<tr key = {data.id}>
		          					<td>{data.id}</td>
		          					<td>{data.name}</td>
		          					<td>{data.email}</td>
		          					<td><a href="JavaScript:Void(0);"  onClick={(e)=>this.handleEdit(data.id)}>EDIT</a><br />
		          						<a href="JavaScript:Void(0);" onClick={(e)=>this.handleDelete(data.id)}>DELETE</a></td>
		          				</tr>)}
		         	</tbody> 
		         </table>
			</div>
  		)
  }

  Add(){
  	return(
  		<div className="App">
<button onClick={(e)=>this.visible('view')}>View List</button><h4>Add</h4>

				<input type="text" name="name" placeholder="name" value={this.state.userEdit.name} onChange={this.handleChange}/>
				<input type="email" name="email" placeholder="email" value={this.state.userEdit.email} onChange={this.handleChange}/>
				<button name="Update" onClick={this.handleUpdateInfo}>Update</button>

        </div>
  		)
  }

}

export default UserManager;
