import React, { Component } from 'react';


class Hotel extends Component {
	constructor(props) {
    super(props);
    this.state = {  
    				"rooms":1,"adults":1,"children":0
    			 };

   	}

   	manageRoom(actionType){
   		if(actionType == 'add'){
   			this.setState({rooms:this.state.rooms<5 ? this.state.rooms + 1 : 5});
   		}
   		else{
   			this.setState({rooms:this.state.rooms >1 ? this.state.rooms - 1 : 1});
   		}
   	}

   	manageAdult(actionType){
   		console.log(actionType);
   		if(actionType == 'add'){
   			var adultAdd = this.state.adults + 1;
   			var calRoom = Math.ceil((adultAdd + this.state.children)/4);
   			console.log(calRoom)
   			this.setState({adults:adultAdd,rooms:calRoom});
   		}
   		else{

   			var adultMinus = this.state.adults - 1;
   			var calRoom = adultMinus > 1 ? Math.ceil((adultMinus + this.state.children)/4) : 1;
   			console.log(calRoom)
   			this.setState({adults:adultMinus >1 ? adultMinus  : 1,rooms:calRoom});
   		}
   	}

   	manageChild(actionType){
   		if(actionType == 'add'){
   			this.setState({children:this.state.children});
   		}
   		else{
   			this.setState({children:this.state.children >0 ? this.state.children - 1 : 1});
   		}
   	}

  render() {
    return (

       <div>
       <div className="u-clearfix" data-render>
<div className="sb-group__field sb-group__field-rooms">
<div className="bui-stepper" data-bui-component="InputStepper">
<div className="bui-stepper__title-wrapper">
<label className="bui-stepper__title" for="no_rooms">Rooms</label>
</div>
<div className="bui-stepper__wrapper">
<input type="range" className="bui-stepper__input" data-bui-ref="input-stepper-field" id="no_rooms" name="no_rooms" min="1" max="30" value="1" data-group-rooms-count />
<button className="bui-button bui-button--secondary bui-stepper__subtract-button" data-bui-ref="input-stepper-subtract-button" type="button" aria-hidden="true" disabled>
<span className="bui-button__text">-</span>
</button>
<span className="bui-stepper__display" data-bui-ref="input-stepper-value" aria-hidden="true">1</span>
<button className="bui-button bui-button--secondary bui-stepper__add-button" data-bui-ref="input-stepper-add-button" type="button" aria-hidden="true" >
<span className="bui-button__text">+</span>
</button>
</div>
</div>
</div>
<div className="sb-group__field sb-group__field-adults">
<div className="bui-stepper" data-bui-component="InputStepper">
<div className="bui-stepper__title-wrapper">
<label className="bui-stepper__title" for="group_adults">Adults</label>
</div>
<div className="bui-stepper__wrapper">
<input type="range" className="bui-stepper__input" data-bui-ref="input-stepper-field" id="group_adults" name="group_adults" min="1" max="30" value="2" data-group-adults-count />
<button className="bui-button bui-button--secondary bui-stepper__subtract-button" data-bui-ref="input-stepper-subtract-button" type="button" aria-hidden="true" >
<span className="bui-button__text">-</span>
</button>
<span className="bui-stepper__display" data-bui-ref="input-stepper-value" aria-hidden="true">2</span>
<button className="bui-button bui-button--secondary bui-stepper__add-button" data-bui-ref="input-stepper-add-button" type="button" aria-hidden="true" >
<span className="bui-button__text">+</span>
</button>
</div>
</div>
</div>
<div className="sb-group__field sb-group-children ">
<div className="bui-stepper" data-bui-component="InputStepper">
<div className="bui-stepper__title-wrapper">
<label className="bui-stepper__title" for="group_children">Children</label>
</div>
<div className="bui-stepper__wrapper">
<input type="range" className="bui-stepper__input" data-bui-ref="input-stepper-field" id="group_children" name="group_children" min="0" max="10" value="0" data-group-children-count />
<button className="bui-button bui-button--secondary bui-stepper__subtract-button" data-bui-ref="input-stepper-subtract-button" type="button" aria-hidden="true" disabled>
<span className="bui-button__text">-</span>
</button>
<span className="bui-stepper__display" data-bui-ref="input-stepper-value" aria-hidden="true">0</span>
<button className="bui-button bui-button--secondary bui-stepper__add-button" data-bui-ref="input-stepper-add-button" type="button" aria-hidden="true" >
<span className="bui-button__text">+</span>
</button>
</div>
</div>
</div>
</div><div>
       <h2>Choose number of people</h2>
       <p>Rooms &nbsp;&nbsp;&nbsp;&nbsp;<a href="Javascript:Void(0);" onClick={(e)=>this.manageRoom('add')}>add</a>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{this.state.rooms}&nbsp;&nbsp;&nbsp;<a href="Javascript:Void(0);"  onClick={(e)=>this.manageRoom('minus')}>minus</a></p>
       <p>Adults&nbsp;&nbsp;&nbsp; <a href="Javascript:Void(0);" onClick={(e)=>this.manageAdult('add')}>add</a>&nbsp;&nbsp;&nbsp;{this.state.adults}&nbsp;&nbsp;&nbsp;<a href="Javascript:Void(0);"  onClick={(e)=>this.manageAdult('minus')}>minus</a></p>
       <p>Children &nbsp;&nbsp;&nbsp;<a href="Javascript:Void(0);" onClick={(e)=>this.manageChild('add')}>add</a>&nbsp;&nbsp;&nbsp;{this.state.children}&nbsp;&nbsp;&nbsp;<a href="Javascript:Void(0);">minus</a></p>
     <a href="Javascript:Void(0);" onClick={(e)=>this.manageChild('minus')}> <button className="bui-button bui-button--secondary bui-stepper__subtract-button" data-bui-ref="input-stepper-subtract-button" type="button" aria-hidden="true">
<span className="bui-button__text">-</span>
</button></a>
<span className="bui-stepper__display" data-bui-ref="input-stepper-value" aria-hidden="true">{this.state.children}</span>
<a href="Javascript:Void(0);"  onClick={(e)=>this.manageChild('add')}><button className="bui-button bui-button--secondary bui-stepper__add-button" data-bui-ref="input-stepper-add-button" type="button" aria-hidden="true" >
<span className="bui-button__text">+</span>
</button></a>
       </div>
       </div>
      );
  }
}

export default Hotel;
