import React, { Component } from 'react';
//import { BrowserRouter as Router, Route, Link } from "react-router-dom";
//import Newcard from './Newcard';


class UserManager extends Component {
constructor(props) {
    super(props);
    this.state = {  
    				data : [
    						{name:"xyz",email:"xyz@xyz.com",id:1},
    						{name:"abc",email:"abc@abc.com",id:2}
    						],
    				userEdit : {name:"",email:"",id:""}
    			 };

   	this.handleChange = this.handleChange.bind(this);
  }

  handleDelete(id){
  	console.log(id)
 // var data = 	this.state.data.filter(data => data.id !== id );
  	this.setState({data : this.state.data.filter(data => data.id !== id )});
//console.log(data)
  }

  handleEdit(id){
  	var user = this.state.data.filter(data => data.id === id );
  	// console.log(user[0])
  	// var userObj = user[0];
  	this.setState({userEdit:user[0]});
  }

  handleChange(e){
  		//var inputData = {[e.target.name]:e.target.value};
  		this.setState({userEdit:{[e.target.name]:e.target.value}});
  }

  render() {
    return (

       <div className="App"><h4>List</h4>
          <table border="2"><tbody>
          	<tr><th>Id</th><th>Name</th><th>Email</th><th>Action</th></tr>
          	{this.state.data.map((data)=> 
          				<tr key = {data.id}>
          					<td>{data.id}</td>
          					<td>{data.name}</td>
          					<td>{data.email}</td>
          					<td><a href="JavaScript:Void(0);"  onClick={(e)=>this.handleEdit(data.id)}>EDIT</a><br />
          						<a href="JavaScript:Void(0);" onClick={(e)=>this.handleDelete(data.id)}>DELETE</a></td>
          				</tr>)}
         </tbody> </table>
			<div>
				<input type="text" name="name" placeholder="name" value={this.state.userEdit.name} onChange={this.handleChange}/>
				<input type="email" name="email" placeholder="email" value={this.state.userEdit.email} onChange={this.handleChange}/>
				<button name="Update">Update</button>
			</div>
        </div>

    );
  }
}

export default UserManager;
