class Hotel extends Component {
	constructor(props) {
    super(props);
    this.state = {  
    				"rooms":1,"adults":1,"children":0
    			 };

   	}

  render() {
    return (

       <div>
       <h2>Choose number of people</h2>
       <p>Rooms &nbsp;&nbsp;&nbsp;&nbsp;<a href="Javascript:Void(0);">add</a>&nbsp;&nbsp;&nbsp;{this.state.rooms}&nbsp;&nbsp;&nbsp;<a href="Javascript:Void(0);">minus</a></p>
       <p>Adults <a href="Javascript:Void(0);">add</a>{this.state.adults}<a href="Javascript:Void(0);">minus</a></p>
       <p>Children <a href="Javascript:Void(0);">add</a>{this.state.children}<a href="Javascript:Void(0);">minus</a></p>
       </div>
      );
  }
}

export default Hotel;
